mapper.select:
SELECT B.BOARD_ID, B.BOARD_TITLE, M.NICKNAME, B.VIEW_COUNT, B.CREATED_DATE, C.CATEGORY_ID, C.CATEGORY_NAME FROM BOARD B JOIN MEMBER M ON B.MEMBER_ID = M.MEMBER_ID JOIN CATEGORY C ON B.CATEGORY_ID = C.CATEGORY_ID WHERE B.CATEGORY_ID = #{categoryId}
AND ( B.BOARD_TITLE LIKE CONCAT('%', #{searchKeyword}, '%') OR B.BOARD_CONTENT LIKE CONCAT('%', #{searchKeyword}, '%') )
ORDER BY B.VIEW_COUNT DESC
ORDER BY B.CREATED_DATE DESC
LIMIT #{limit} OFFSET #{offset};

SELECT BM.BOARD_ID, B.BOARD_TITLE
FROM BOOKMARK BM
         JOIN BOARD B ON BM.BOARD_ID = B.BOARD_ID
WHERE BM.MEMBER_ID = #{memberId} LIMIT #{limit}
OFFSET #{offset}

SELECT COUNT(*)
FROM BOOKMARK
WHERE MEMBER_ID = #{memberId}
  AND BOARD_ID = #{boardId}

mapper.delete:
DELETE
FROM BOOKMARK
WHERE MEMBER_ID = #{memberId}
  AND BOARD_ID = #{boardId}

mapper.update:
UPDATE BOARD
SET BOARD_TITLE   = #{boardTitle},
    BOARD_CONTENT = #{boardContent},
    BOARD_IMG     = #{boardImg},
    CATEGORY_ID   = #{categoryId}
WHERE BOARD_ID = #{boardId}
